mapper.select:
SELECT CODE_NAME ,BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT CODE_NAME ,BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD B JOIN COM_CODE C ON B.BOARD_TYPE = C.CODE_ID WHERE C.CODE_ID IN
#{boardType}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.delete:
 	Delete
 	FROM
 		BOARD
		WHERE	BOARD_TYPE = #{boardType}
 	AND 	BOARD_NUM  = #{boardNum}
